# Re do CSfrequencies with only CS8, CS3, CS15, CS6, CS2 and CS5 and only for GPP

training_set_GPP_Redo <- training_set[,1:24]
At.hiHMM_CS_GPP_Redo <- At.hiHMM_CS[c(2,3,5,6,8,15)]
At.All_GPP_Redo <- At.All[c(1,4,5)]

for(i in 1:nrow(training_set_GPP_Redo)){
  
  print(paste0("Analysing gene number ", i, " | Percentage: ", i/nrow(training_set_GPP_Redo)*100))
  
  ## Part 0: search hits for each 
  
  # hit for the gene
  
  hit1.gene <- which(names(At.genes) == training_set_GPP_Redo$Gene1[i])
  hit2.gene <- which(names(At.genes) == training_set_GPP_Redo$Gene2[i])
  
  # extract promoter +- 1000 nt for promoter | promoter upstream 1000 nt
  
  hit1.promoter <- which(names(At.promoters) == training_set_GPP_Redo$Gene1[i])
  hit2.promoter <- which(names(At.promoters) == training_set_GPP_Redo$Gene2[i])
  
  hit1.promoter.2 <- which(names(At.promoters.2) == training_set_GPP_Redo$Gene1[i])
  hit2.promoter.2 <- which(names(At.promoters.2) == training_set_GPP_Redo$Gene2[i])
  
  ## Part 1: frequency vectors | absence presence vectors and results
  
  Freq.vectors.1 <- list(genes = list(), promoter = list(), promoter.2 = list())
  Freq.vectors.2 <- list(genes = list(), promoter = list(), promoter.2 = list())
  hits.1 <- list(gene = hit1.gene, promoter = hit1.promoter, promoter.2 = hit1.promoter.2)
  hits.2 <- list(gene = hit2.gene, promoter = hit2.promoter, promoter.2 = hit2.promoter.2)
  
  for(z in 1:length(At.hiHMM_CS_GPP_Redo)){
    for(j in 1:length(At.All_GPP_Redo)){
      
      ## hit1
      
      # frequency vectors
      
      hits <- findOverlaps(At.All_GPP_Redo[[j]][hits.1[[j]]], At.hiHMM_CS_GPP_Redo[[z]])
      overlaps <- pintersect(At.All_GPP_Redo[[j]][hits.1[[j]]][queryHits(hits)], At.hiHMM_CS_GPP_Redo[[z]][subjectHits(hits)])
      percentOverlap <- width(overlaps) / width(At.All_GPP_Redo[[j]][hits.1[[j]]][queryHits(hits)])    
      if(length(percentOverlap) == 0){
        percentOverlap <- 0
      }
      
      Freq.vectors.1[[j]][[z]] <- percentOverlap
      
      ## hit2
      
      # frequency vectors
      
      hits <- findOverlaps(At.All_GPP_Redo[[j]][hits.2[[j]]], At.hiHMM_CS_GPP_Redo[[z]])
      overlaps <- pintersect(At.All_GPP_Redo[[j]][hits.2[[j]]][queryHits(hits)], At.hiHMM_CS_GPP_Redo[[z]][subjectHits(hits)])
      percentOverlap <- width(overlaps) / width(At.All_GPP_Redo[[j]][hits.2[[j]]][queryHits(hits)])  
      if(length(percentOverlap) == 0){
        percentOverlap <- 0
      }
      
      Freq.vectors.2[[j]][[z]] <- percentOverlap
      
    }
  }
  
  
  ## unlist each sublist
  
  Freq.vectors.1F <- lapply(Freq.vectors.1, function(x){
    lapply(x, function(y){
      sum(y)
    })
  })
  
  Freq.vectors.2F <- lapply(Freq.vectors.2, function(x){
    lapply(x, function(y){
      sum(y)
    })
  })
  
  BinF.vectors.1F <- lapply(Freq.vectors.1F, function(x){
    lapply(x, function(y){
      if(y > 0){
        y <- 1
        y
      }else if(y == 0){
        y <- 0
        y
      }
    })
  })
  
  BinF.vectors.2F <- lapply(Freq.vectors.2F, function(x){
    lapply(x, function(y){
      if(y > 0){
        y <- 1
        y
      }else if(y == 0){
        y <- 0
        y
      }
    })
  })
  
  
  ## Part2 Calculate distance and simmiliraty
  
  # Frequency vectors
  
  training_set_GPP_Redo$Pearson_CSfrequency_gene[i] <- cor(x = unlist(Freq.vectors.1F$genes), y = unlist(Freq.vectors.2F$genes),method = "pearson")
  training_set_GPP_Redo$Pearson_CSfrequency_promoter[i] <- cor(x = unlist(Freq.vectors.1F$promoter), y = unlist(Freq.vectors.2F$promoter),method = "pearson")
  training_set_GPP_Redo$Pearson_CSfrequency_promoter.2[i] <- cor(x = unlist(Freq.vectors.1F$promoter.2), y = unlist(Freq.vectors.2F$promoter.2),method = "pearson")
  
  training_set_GPP_Redo$Pearson_CSfrequency_gene_absolute[i] <- abs(cor(x = unlist(Freq.vectors.1F$genes), y = unlist(Freq.vectors.2F$genes),method = "pearson"))
  training_set_GPP_Redo$Pearson_CSfrequency_promoter_absolute[i] <- abs(cor(x = unlist(Freq.vectors.1F$promoter), y = unlist(Freq.vectors.2F$promoter),method = "pearson"))
