    }else if(length(unique(training_set_transform_GPP_F[,"Group"])) != 1){
      RD9.binary <- fisher.test(table(training_set_transform_GPP_F[,c("Class", "Group")]))
      CSMetrics_GPP_results[i,7] <- RD9.binary$p.value
      CSMetrics_GPP_results[i,8] <- as.numeric(RD9.binary$estimate)
    }
    ## RD4 Fisher
