      CSMetrics_GPP_results[i,3] <- RD4.binary$p.value
      CSMetrics_GPP_results[i,4] <- as.numeric(RD4.binary$estimate)
    }
  }
  ## return colname
  colnames(training_set_transform_GPP_F)[col.hit] <- prev.name
}
